import { useEffect, useState } from 'react';
import { Space, Image, Card, Badge, Row, Col, Button, notification, Pagination, Divider } from 'antd';
import Cookies from 'universal-cookie';
import { CloudDownloadOutlined, LinkOutlined } from '@ant-design/icons';
import { useParams } from "react-router-dom";

const { Meta } = Card;
interface IDaImagesViewProps {
    apiUrl: string
}

interface IGalleryGet {
    ID: number,
    IMAGE_INDEX: string,
    IMAGE_TITLE: number,
    DIRECTORY_NAME: string,
    FILENAME: string
}

const cookies = new Cookies();

const DaImagesView = (props: IDaImagesViewProps) => {
    const [imageData, setImageData] = useState<IGalleryGet[]>([])
    const [imgSize, setImgSize] = useState<number>(200)
    const [pageNum, setPageNum] = useState<number>(1)

    let params = useParams();

    const loadImages = async (page_num:number) => {
        const response = await fetch(`${props.apiUrl}/api/da/gallery/images/${params.galleryid}?p=${page_num}`);
        const galleries = await response.json();
        setImageData(galleries)
    }

    useEffect(() => {

        loadImages(pageNum - 1)

        const cookImgSize = cookies.get("imgSize")
        if (typeof cookImgSize === "string") {
            const cookImgNumSize = Number(cookImgSize)
            if (cookImgNumSize >= 100 && cookImgNumSize <= 500)
                setImgSize(cookImgNumSize)

        }

    }, []);

    const generateGalleryCard = (gallData: IGalleryGet) => {
        return (
                <Card
                style={{ width: imgSize, height: imgSize + 100 }}
                cover={
                    generateGalleryImage(gallData)
                }
            >
                <Meta style={{ position: "relative", bottom: 0, left: 0, margin: "10px" }}
                    title={`${gallData.IMAGE_TITLE}`}
                    description={`${gallData.IMAGE_INDEX}`}
                />
            </Card>
        )
    }


    const generateGalleryImage = (gallData: IGalleryGet) => {
        return (
            <Image
                width="100%"
                height={imgSize}
                src={`${props.apiUrl}/da/${gallData.DIRECTORY_NAME}/${gallData.FILENAME}.webp`}
                fallback="data:image/png;base64,"
            />

        )
    }
    const onPageChange = (page_num: number, page_size: number) => {
        setPageNum(page_num)
        loadImages(page_num - 1)
    }

    return (
        <>
            <Row>
                <Col span={24}>
                    <Space size={[8, 16]} wrap>
                        {imageData.map((imgdata) => generateGalleryCard(imgdata))}
                        {imageData.length == 0 ? "Nothing Here...." : ""}
                    </Space>
                </Col>
            </Row>
            <Divider/>
            <Row>
                <Col>
                    <Pagination onChange={onPageChange} current={pageNum} total={50} />
                </Col>
            </Row>
        </>
    )


}


export default DaImagesView;